<?php class Mailer {    var $mail;    public function __construct()    {        require_once('PHPMailer/class.phpmailer.php');                $this->mail = new PHPMailer(true);        $this->mail->IsSMTP();        $this->mail->CharSet = "utf-8";         $this->mail->IsHTML(true);        $this->mail->SMTPDebug  = 0;                             $this->mail->SMTPAuth   = true;                         $this->mail->SMTPSecure = "ssl";                       $this->mail->Host       = "smtp.gmail.com";              $this->mail->Port       = 465;                           $this->mail->Username   = "vietmongco.vn@gmail.com";        $this->mail->Password   = "vietmongco";               $this->mail->AddReplyTo('vietmongco.vn@gmail.com', 'MITSUVNAUTOPARTS');        $this->mail->SetFrom('vietmongco.vn@gmail.com', 'MITSUVNAUTOPARTS');    }    public function sendmail($to, $to_name, $subject, $body){        try{            $this->mail->AddAddress($to, $to_name);            $this->mail->Subject = $subject;            $this->mail->Body    = $body;            $this->mail->Send();        } catch (phpmailerException $e) {            echo $e->errorMessage();         } catch (Exception $e) {            echo $e->getMessage();         }    }}?>